import { Todo } from './../todo';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css']
})
export class TodosComponent {

  @Input() todo: Todo = {
    id: 0,
    conteudo: "teste 2",
    modelo: "modelo1"
  }

  widthTodo():string{
    if(this.todo.conteudo.length >= 256){
      return 'todo-g'
    }else{
      return 'todo-p'
    }
  }
}
<div class="model {{todo.modelo}} ff-roboto-mono [(ngClass)]='widthTodo()'">
    <img src="../../../../assets/img/{{todo.modelo}}.svg" alt="aspas coloridas" width="35">
    <p class="content">{{todo.conteudo}}</p>
    <div class="acoes">
        <button class="botao-editar" >
            <img src="../../../assets/img/icone-editar.png" alt="ícone editar">
        </button>
        <button class="botao-excluir" >
            <img src="../../../assets/img/icone-excluir.svg" alt="ícone excluir" width="35">
        </button>
    </div>
</div>
